import axios from "axios";

const API_BASE_URL = "http://localhost:3001/api";

const api = axios.create({
	baseURL: API_BASE_URL,
	headers: {
		"Content-Type": "application/json",
	},
});

export const gameService = {
	getPlayer: async () => {
		try {
			const response = await api.get("/game/player");
			return response.data;
		} catch (error) {
			console.error("Error fetching player:", error);
			throw error;
		}
	},
	validateAnswer: async (playerId, answer) => {
		try {
			const response = await api.post("/game/validate", { playerId, answer });
			return response.data;
		} catch (error) {
			console.error("Error validating answer:", error);
			throw error;
		}
	},
	submitScore: (score) => api.post("/game/score", { score }),
	resetGame: () => api.post("/game/reset"),
};

export default api;
