import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './FootballGame.css';

const FootballGame = () => {
  const [gameState, setGameState] = useState({
    currentPlayer: null,
    playersLeft: 42,
    score: 0,
    wildcardAvailable: true,
    selectedChoices: new Set(),
    gameOver: false
  });

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchNextPlayer();
  }, []);

  const fetchNextPlayer = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/game/player');
      setGameState(prev => ({
        ...prev,
        currentPlayer: response.data,
        selectedChoices: new Set()
      }));
    } catch (error) {
      console.error('Error fetching player:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleChoice = async (choiceId) => {
    if (gameState.selectedChoices.has(choiceId)) return;

    try {
      const response = await axios.post('/api/game/validate', {
        playerId: gameState.currentPlayer.id,
        answer: choiceId
      });

      setGameState(prev => ({
        ...prev,
        score: response.data.isCorrect ? prev.score + 10 : prev.score,
        selectedChoices: new Set([...prev.selectedChoices, choiceId])
      }));

      if (response.data.isCorrect) {
        // Visual feedback for correct answer
        // You might want to add some animation here
      }
    } catch (error) {
      console.error('Error validating choice:', error);
    }
  };

  const handleSkip = () => {
    setGameState(prev => ({
      ...prev,
      playersLeft: prev.playersLeft - 1
    }));

    if (gameState.playersLeft <= 1) {
      endGame();
    } else {
      fetchNextPlayer();
    }
  };

  const useWildcard = () => {
    if (!gameState.wildcardAvailable) return;

    // Reveal one correct answer
    const correctAnswer = gameState.currentPlayer.correctAnswers[0];
    setGameState(prev => ({
      ...prev,
      wildcardAvailable: false,
      selectedChoices: new Set([...prev.selectedChoices, correctAnswer])
    }));
  };

  const endGame = () => {
    setGameState(prev => ({
      ...prev,
      gameOver: true
    }));
    // You might want to navigate to a results page or show a modal
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (gameState.gameOver) {
    return (
      <div className="game-over">
        <h2>Game Over!</h2>
        <p>Final Score: {gameState.score}</p>
        <button onClick={() => window.location.reload()}>Play Again</button>
      </div>
    );
  }

  const choices = [
    { id: 1, label: "EUROS WINNER", icon: "🏆" },
    { id: 2, label: "COPA AMERICA WINNER\nSINCE 1960", icon: "🏆" },
    { id: 3, label: "NLD", icon: "🇳🇱" },
    { id: 4, label: "GER", icon: "🇩🇪" },
    { id: 5, label: "INT", icon: "⚫🔵" },
    { id: 6, label: "WHU", icon: "⚒️" },
    { id: 7, label: "BRA x MIL", icon: "🇧🇷" },
    { id: 8, label: "JUV", icon: "⚫⚪" },
    { id: 9, label: "ITA", icon: "🇮🇹" },
    { id: 10, label: "MIL", icon: "🔴⚫" },
    { id: 11, label: "BO4", icon: "⚫🔴" },
    { id: 12, label: "ITALIAN TOP DIV GOLDEN\nBOOT WINNER SINCE 1959/61", icon: "👟" },
    { id: 13, label: "BRA", icon: "🇧🇷" },
    { id: 14, label: "PLAYED IN DUTCH TOP\nDIV SINCE 1956/57", icon: "🇳🇱" },
    { id: 15, label: "MANAGED BY\nANCELOTTI", icon: "👔" },
    { id: 16, label: "PLAYED WITH RIBERY", icon: "🏃" },
  ];

  return (
    <div className="football-game">
      <div className="game-header">
        <div className="player-info">
          <div className="player-number">{gameState.currentPlayer?.id}</div>
          <div className="player-name">{gameState.currentPlayer?.name}</div>
          <button className="wildcard-btn" onClick={useWildcard}>
            Play Wildcard <span className="info-icon">ⓘ</span>
          </button>
        </div>
        <div className="game-controls">
          <button className="skip-btn" onClick={handleSkip}>
            SKIP ▶
          </button>
          <div className="players-left">{gameState.playersLeft} PLAYERS LEFT</div>
        </div>
      </div>

      <div className="choices-grid">
        {choices.map((choice) => (
          <div
            key={choice.id}
            className="choice-card"
            onClick={() => handleChoice(choice.id)}
          >
            <span className="choice-icon">{choice.icon}</span>
            <span className="choice-label">{choice.label}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FootballGame;
