import React, { useState, useEffect } from 'react';
import { gameService } from '../../services/api';
import './FootballGame.css';

const FootballGame = () => {
  const [gameState, setGameState] = useState({
    currentPlayer: null,
    choices: [],
    playersLeft: 42,
    loading: true,
    error: null,
    gameOver: false
  });

  // Keep track of all selected choices throughout the game
  const [globalSelectedChoices, setGlobalSelectedChoices] = useState(new Set());
  // Keep track of correct/incorrect choices
  const [choiceResults, setChoiceResults] = useState({});

  // Add new state for modal
  const [showScoreModal, setShowScoreModal] = useState(false);
  const [finalScore, setFinalScore] = useState(0);

  // Add new state for tracking API calls
  const [isProcessing, setIsProcessing] = useState(false);

  useEffect(() => {
    // Reset game on component mount (page refresh)
    const resetAndStartGame = async () => {
      try {
        await gameService.resetGame();
        setGlobalSelectedChoices(new Set());
        setChoiceResults({});
        setShowScoreModal(false);
        setFinalScore(0);
        setIsProcessing(false);
        setGameState({
          currentPlayer: null,
          choices: [],
          playersLeft: 42,
          loading: true,
          error: null,
          gameOver: false
        });
        // fetchPlayerData();
      } catch (error) {
        console.error('Error resetting game:', error);
        setGameState(prev => ({
          ...prev,
          error: 'Failed to reset game',
          loading: false
        }));
      }
    };

    resetAndStartGame();
  }, []); // Empty dependency array means this runs once on mount

  useEffect(() => {
    fetchPlayerData();
  }, []);

  const fetchPlayerData = async () => {
    try {
      const data = await gameService.getPlayer();

      if (!data.player) {
        setGameState(prev => ({
          ...prev,
          gameOver: true,
          loading: false
        }));
        return;
      }

      setGameState(prev => ({
        ...prev,
        currentPlayer: data.player,
        choices: data.choices,
        playersLeft: data.playersLeft,
        loading: false
      }));
    } catch (error) {
      if (error.response?.status === 404) {
        setGameState(prev => ({
          ...prev,
          gameOver: true,
          loading: false
        }));
      } else {
        setGameState(prev => ({
          ...prev,
          error: 'Failed to load player data',
          loading: false
        }));
      }
    }
  };

  const handleChoice = async (choiceId) => {
    // Prevent selecting if already processing or already chosen
    if (isProcessing || globalSelectedChoices.has(choiceId)) return;

    try {
      setIsProcessing(true); // Set processing state before API call

      const response = await gameService.validateAnswer(
        gameState.currentPlayer.id,
        choiceId
      );

      // Update global selected choices
      setGlobalSelectedChoices(prev => new Set([...prev, choiceId]));

      // Update choice results
      setChoiceResults(prev => ({
        ...prev,
        [choiceId]: response.isCorrect
      }));

      // Check if all options are selected
      const updatedSelectedOptions = [...globalSelectedChoices, choiceId];
      if (updatedSelectedOptions.length === gameState.choices.length) {
        const score = calculateFinalScore();
        setFinalScore(score);
        setShowScoreModal(true);
      }

      // Move to next player after a short delay
      setTimeout(async () => {
        try {
          // Fetch next player data
          const data = await gameService.getPlayer();

          if (!data.player) {
            setGameState(prev => ({
              ...prev,
              gameOver: true,
              loading: false
            }));
          } else {
            setGameState(prev => ({
              ...prev,
              currentPlayer: data.player,
              choices: data.choices,
              playersLeft: data.playersLeft,
              loading: false
            }));
          }
        } catch (error) {
          console.error('Error fetching next player:', error);
          setGameState(prev => ({
            ...prev,
            error: 'Failed to load next player',
            loading: false
          }));
        } finally {
          setIsProcessing(false); // Reset processing state
        }
      }, 1000); // 1 second delay to show the result

    } catch (error) {
      console.error('Error validating choice:', error);
      setIsProcessing(false); // Reset processing state on error
    }
  };

  const handleNextPlayer = () => {
    if (gameState.playersLeft <= 1) {
      setGameState(prev => ({
        ...prev,
        gameOver: true
      }));
      return;
    }

    setGameState(prev => ({
      ...prev,
      loading: true
    }));
    fetchPlayerData();
  };

  const handleSkip = () => {
    handleNextPlayer();
  };

  const handleRestart = async () => {
    try {
      await gameService.resetGame();
      setGlobalSelectedChoices(new Set());
      setChoiceResults({});
      setGameState({
        currentPlayer: null,
        choices: [],
        playersLeft: 42,
        loading: true,
        error: null,
        gameOver: false
      });
      fetchPlayerData();
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  const calculateFinalScore = () => {
    // Calculate score based on correct answers
    return Object.values(choiceResults).filter(Boolean).length;
  };

  const handleCloseModal = () => {
    setShowScoreModal(false);
    // Optional: Add navigation to home or restart game
  };

  if (gameState.loading) {
    return <div className="loading">Loading...</div>;
  }

  if (gameState.error) {
    return <div className="error">{gameState.error}</div>;
  }

  if (gameState.gameOver) {
    return (
      <div className="game-over">
        <h2>Game Over!</h2>
        <p>You've seen all players!</p>
        <button onClick={handleRestart} className="restart-btn">
          Play Again
        </button>
      </div>
    );
  }

  return (
    <div className="football-game">
      <div className="game-header">
        <div className="player-info">
          <div className="player-number">{gameState.currentPlayer?.id}</div>
          <div className="player-name">{gameState.currentPlayer?.name}</div>
          <button className="wildcard-btn">
            Play Wildcard <span className="info-icon">ⓘ</span>
          </button>
        </div>
        <div className="game-controls">
          <button className="skip-btn" onClick={handleSkip}>
            SKIP ▶
          </button>
          <div className="players-left">{gameState.playersLeft} PLAYERS LEFT</div>
        </div>
      </div>

      <div className="choices-grid">
        {gameState.choices.map((choice) => (
          <div
            key={choice.id}
            className={`choice-card ${
              globalSelectedChoices.has(choice.id)
                ? choiceResults[choice.id]
                  ? 'correct selected'
                  : 'incorrect selected'
                : ''
            } ${isProcessing ? 'disabled' : ''}`}
            onClick={() => handleChoice(choice.id)}
          >
            <span className="choice-icon">{choice.icon}</span>
            <span className="choice-label">{choice.label}</span>
          </div>
        ))}
      </div>

      {/* Add Score Modal */}
      {showScoreModal && (
        <div className="score-modal-overlay">
          <div className="score-modal">
            <h2>Game Complete!</h2>
            <p>Your Final Score: {finalScore}</p>
            <button onClick={handleCloseModal}>Close</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FootballGame;
