import React, { useState, useEffect } from 'react';
import { gameService } from '../../services/api';
import './FootballGame.css';
import CardSelector from '../CardSelector/CardSelector';

const FootballGame = () => {
  const [gameState, setGameState] = useState({
    currentPlayer: null,
    choices: [],
    playersLeft: 42,
    loading: true,
    error: null,
    gameOver: false
  });

  // Keep track of all selected choices throughout the game
  const [globalSelectedChoices, setGlobalSelectedChoices] = useState(new Set());
  // Keep track of correct/incorrect choices
  const [choiceResults, setChoiceResults] = useState({});

  // Add new state for modal
  const [showScoreModal, setShowScoreModal] = useState(false);
  const [finalScore, setFinalScore] = useState(0);

  // Add new state for tracking API calls
  const [isProcessing, setIsProcessing] = useState(false);

  const [selectedCard, setSelectedCard] = useState(null);
  const [availableCards, setAvailableCards] = useState([]);

  useEffect(() => {
    // Reset game on component mount (page refresh)
    const resetAndStartGame = async () => {
      try {
        await gameService.resetGame();
        setGlobalSelectedChoices(new Set());
        setChoiceResults({});
        setShowScoreModal(false);
        setFinalScore(0);
        setIsProcessing(false);
        setGameState({
          currentPlayer: null,
          choices: [],
          playersLeft: 42,
          loading: true,
          error: null,
          gameOver: false
        });
        // fetchPlayerData();
      } catch (error) {
        console.error('Error resetting game:', error);
        setGameState(prev => ({
          ...prev,
          error: 'Failed to reset game',
          loading: false
        }));
      }
    };

    resetAndStartGame();
  }, []); // Empty dependency array means this runs once on mount

  useEffect(() => {
    fetchPlayerData();
  }, []);

  useEffect(() => {
    const fetchCards = async () => {
      try {
        const cards = await gameService.getCards();
        setAvailableCards(cards);
      } catch (error) {
        console.error('Error fetching cards:', error);
      }
    };

    fetchCards();
  }, []);

  const fetchPlayerData = async () => {
    if (!selectedCard) return;

    try {
      const data = await gameService.getPlayer(selectedCard);

      if (!data.player) {
        setGameState(prev => ({
          ...prev,
          gameOver: true,
          loading: false
        }));
        return;
      }

      setGameState(prev => ({
        ...prev,
        currentPlayer: data.player,
        choices: data.choices,
        playersLeft: data.playersLeft,
        loading: false
      }));
    } catch (error) {
      if (error.response?.status === 404) {
        setGameState(prev => ({
          ...prev,
          gameOver: true,
          loading: false
        }));
      } else {
        setGameState(prev => ({
          ...prev,
          error: 'Failed to load player data',
          loading: false
        }));
      }
    }
  };

  const handleGameEnd = async () => {
    // Calculate score before resetting states
    const correctAnswers = Object.values(choiceResults).filter(Boolean).length;
    const totalAnswers = Object.keys(choiceResults).length;

    setFinalScore(correctAnswers);
    setShowScoreModal(true);

    try {
      await gameService.resetGame();
      // Don't reset states until modal is closed
      setGameState({
        currentPlayer: null,
        choices: [],
        playersLeft: 42,
        loading: false,
        error: null,
        gameOver: false
      });
    } catch (error) {
      console.error('Error resetting game:', error);
      setGameState(prev => ({
        ...prev,
        error: 'Failed to reset game'
      }));
    }
  };

  const handleCloseModal = async () => {
    // Reset states when modal is closed
    setShowScoreModal(false);
    setGlobalSelectedChoices(new Set());
    setChoiceResults({});
    setIsProcessing(false);
    setFinalScore(0);

    // Fetch first player of new game
    try {
      setGameState(prev => ({ ...prev, loading: true }));
      const data = await gameService.getPlayer();
      setGameState({
        currentPlayer: data.player,
        choices: data.choices,
        playersLeft: data.playersLeft,
        loading: false,
        error: null,
        gameOver: false
      });
    } catch (error) {
      console.error('Error starting new game:', error);
      setGameState(prev => ({
        ...prev,
        error: 'Failed to start new game',
        loading: false
      }));
    }
  };

  const handleChoice = async (choiceId) => {
    if (isProcessing || globalSelectedChoices.has(choiceId)) return;

    try {
      setIsProcessing(true);

      const response = await gameService.validateAnswer(
        gameState.currentPlayer.id,
        choiceId
      );

      // Update global selected choices and results only after successful validation
      setGlobalSelectedChoices(prev => new Set([...prev, choiceId]));
      setChoiceResults(prev => ({
        ...prev,
        [choiceId]: response.isCorrect
      }));

      // Check if all options are selected
      const updatedSelectedOptions = [...globalSelectedChoices, choiceId];
      if (updatedSelectedOptions.length === gameState.choices.length) {
        handleGameEnd();
        return;
      }

      // Move to next player after a short delay
      setTimeout(async () => {
        try {
          const data = await gameService.getPlayer();

          if (!data.player) {
            handleGameEnd();
          } else {
            setGameState(prev => ({
              ...prev,
              currentPlayer: data.player,
              choices: data.choices,
              playersLeft: data.playersLeft,
              loading: false
            }));
          }
        } catch (error) {
          console.error('Error fetching next player:', error);
          setGameState(prev => ({
            ...prev,
            error: 'Failed to load next player',
            loading: false
          }));
        } finally {
          setIsProcessing(false);
        }
      }, 1500); // Increased delay to 1.5 seconds

    } catch (error) {
      console.error('Error validating choice:', error);
      // Check if it's a rate limit error
      if (error.response?.status === 429) {
        // Show some user feedback about rate limiting
        setGameState(prev => ({
          ...prev,
          error: 'Please wait before selecting another option'
        }));
        setTimeout(() => {
          setGameState(prev => ({ ...prev, error: null }));
        }, 2000);
      }
      setIsProcessing(false);
    }
  };

  const handleNextPlayer = () => {
    if (gameState.playersLeft <= 1) {
      setGameState(prev => ({
        ...prev,
        gameOver: true
      }));
      return;
    }

    setGameState(prev => ({
      ...prev,
      loading: true
    }));
    fetchPlayerData();
  };

  const handleSkip = () => {
    handleNextPlayer();
  };

  const handleRestart = async (cardId) => {
    try {
      await gameService.resetGame();
      setGlobalSelectedChoices(new Set());
      setChoiceResults({});
      setGameState({
        currentPlayer: null,
        choices: [],
        playersLeft: 42,
        loading: true,
        error: null,
        gameOver: false
      });
      setSelectedCard(cardId);
      fetchPlayerData();
    } catch (error) {
      console.error('Error resetting game:', error);
    }
  };

  const calculateFinalScore = () => {
    // Calculate score based on correct answers
    return Object.values(choiceResults).filter(Boolean).length;
  };

  const handleCardSelect = (cardId) => {
    setSelectedCard(cardId);
    // Reset game state and fetch first player with selected card
    handleRestart(cardId);
  };

  if (gameState.loading) {
    return <div className="loading">Loading...</div>;
  }

  if (gameState.error) {
    return <div className="error">{gameState.error}</div>;
  }

  if (gameState.gameOver) {
    return (
      <div className="game-over">
        <h2>Game Over!</h2>
        <p>You've seen all players!</p>
        <button onClick={() => handleRestart(selectedCard)} className="restart-btn">
          Play Again
        </button>
      </div>
    );
  }

  if (!selectedCard) {
    return <CardSelector cards={availableCards} onSelectCard={handleCardSelect} />;
  }

  return (
    <div className="football-game">
      <div className="game-header">
        <div className="player-info">
          {/* <div className="player-number"></div> */}
          <div className="player-name">{gameState.currentPlayer?.name}</div>
          {/* <button className="wildcard-btn">
            Play Wildcard <span className="info-icon">ⓘ</span>
          </button> */}
        </div>
        <div className="game-controls">
          <button className="skip-btn" onClick={handleSkip}>
            SKIP ▶
          </button>
          <div className="players-left">{gameState.playersLeft} PLAYERS LEFT</div>
        </div>
      </div>

      <div className="choices-grid">
        {gameState.choices.map((choice) => (
          <div
            key={choice.id}
            className={`choice-card ${
              globalSelectedChoices.has(choice.id)
                ? choiceResults[choice.id]
                  ? 'correct selected'
                  : 'incorrect selected'
                : ''
            } ${isProcessing ? 'disabled' : ''}`}
            onClick={() => handleChoice(choice.id)}
          >
            <span className="choice-icon">{choice.icon}</span>
            <span className="choice-label">{choice.label}</span>
          </div>
        ))}
      </div>

      {/* Add Score Modal */}
      {showScoreModal && (
        <div className="score-modal-overlay">
          <div className="score-modal">
            <h2>Game Complete!</h2>
            <div className="score-details">
              <p>Correct Answers: {finalScore}</p>
              <p>Total Questions: {Object.keys(choiceResults).length}</p>
              <p>Accuracy: {((finalScore / Object.keys(choiceResults).length) * 100).toFixed(1)}%</p>
            </div>
            <button onClick={handleCloseModal}>Play Again</button>
          </div>
        </div>
      )}
    </div>
  );
};

export default FootballGame;
