import React, { useState, useEffect } from 'react';
import { gameService } from '../../services/api';
import './FootballGame.css';

const FootballGame = () => {
  const [gameState, setGameState] = useState({
    currentPlayer: null,
    choices: [],
    playersLeft: 42,
    selectedChoices: new Set(),
    wildcardAvailable: true,
    loading: true,
    error: null
  });

  const [choiceResults, setChoiceResults] = useState({}); // Track correct/incorrect choices

  useEffect(() => {
    fetchPlayerData();
  }, []);

  const fetchPlayerData = async () => {
    try {
      const data = await gameService.getPlayer();
      setGameState(prev => ({
        ...prev,
        currentPlayer: data.player,
        choices: data.choices,
        playersLeft: data.playersLeft,
        loading: false,
        selectedChoices: new Set()
      }));
      setChoiceResults({}); // Reset choice results for new player
    } catch (error) {
      setGameState(prev => ({
        ...prev,
        error: 'Failed to load player data',
        loading: false
      }));
    }
  };

  const handleChoice = async (choiceId) => {
    // Prevent selecting already chosen options
    if (gameState.selectedChoices.has(choiceId)) return;

    try {
      const response = await gameService.validateAnswer(
        gameState.currentPlayer.id,
        choiceId
      );

      // Update selected choices and their results
      setGameState(prev => ({
        ...prev,
        selectedChoices: new Set([...prev.selectedChoices, choiceId])
      }));

      setChoiceResults(prev => ({
        ...prev,
        [choiceId]: response.isCorrect
      }));

      // If answer is incorrect, move to next player after a delay
      if (!response.isCorrect) {
        setTimeout(() => {
          handleNextPlayer();
        }, 1000); // 1 second delay before moving to next player
      }

    } catch (error) {
      console.error('Error validating choice:', error);
    }
  };

  const handleNextPlayer = () => {
    setGameState(prev => ({
      ...prev,
      playersLeft: prev.playersLeft - 1,
      selectedChoices: new Set(),
      loading: true
    }));
    fetchPlayerData();
  };

  const handleSkip = () => {
    handleNextPlayer();
  };

  const handleWildcard = () => {
    if (!gameState.wildcardAvailable) return;
    setGameState(prev => ({ ...prev, wildcardAvailable: false }));
    // Implement wildcard logic here
  };

  if (gameState.loading) {
    return <div className="loading">Loading...</div>;
  }

  if (gameState.error) {
    return <div className="error">{gameState.error}</div>;
  }

  return (
    <div className="football-game">
      <div className="game-header">
        <div className="player-info">
          <div className="player-number">{gameState.currentPlayer?.id}</div>
          <div className="player-name">{gameState.currentPlayer?.name}</div>
          <button 
            className={`wildcard-btn ${!gameState.wildcardAvailable ? 'disabled' : ''}`}
            onClick={handleWildcard}
            disabled={!gameState.wildcardAvailable}
          >
            Play Wildcard <span className="info-icon">ⓘ</span>
          </button>
        </div>
        <div className="game-controls">
          <button className="skip-btn" onClick={handleSkip}>
            SKIP ▶
          </button>
          <div className="players-left">{gameState.playersLeft} PLAYERS LEFT</div>
        </div>
      </div>

      <div className="choices-grid">
        {gameState.choices.map((choice) => (
          <div
            key={choice.id}
            className={`choice-card ${
              gameState.selectedChoices.has(choice.id)
                ? choiceResults[choice.id]
                  ? 'correct'
                  : 'incorrect'
                : ''
            }`}
            onClick={() => handleChoice(choice.id)}
          >
            <span className="choice-icon">{choice.icon}</span>
            <span className="choice-label">{choice.label}</span>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FootballGame;
